// ==UserScript==
// @name			 Minimalist for Google Calendar
// @author			 Ansel Santosa
// @namespace		http://chrome.google.com/webstore
// @description		The current date in favicon script
// ==/UserScript==

chrome.runtime.sendMessage({elements: "o"}, function(response) {
	if (response.o.favicon) {	
		function updateFavicon() {
			var canvas = document.createElement('canvas'),
			ctx,
			img = document.createElement('img'),
			link = document.createElement('link'),
			today = new Date(),
			day = today.getDate() + '',
			head = document.getElementsByTagName('head')[0];
			if (canvas.getContext) {
				canvas.height = canvas.width = 16; // set the size
				ctx = canvas.getContext('2d');
				function afterLoad() {
					ctx.drawImage(img, 0, 0);
					ctx.font = 'bold 10px "helvetica","Arial",sans-serif';
					ctx.fillStyle = '#F0EEDD';
					if (day.length == 1) day = '0' + day;
					ctx.textAlign = 'center';
					ctx.fillText(day, 8, 13);
					link.setAttribute('type', 'image/png');
					link.setAttribute('rel', 'shortcut icon');
					link.setAttribute('href', canvas.toDataURL('image/png'));
					head.appendChild(link);
				}
				img.addEventListener("load", afterLoad, false);
				img.src = 'data:image/png;base64,';
			}
			var tomorrow = new Date();
			tomorrow.setDate(today.getDate() + 1);
			tomorrow.setHours(0);
			tomorrow.setMinutes(0);
			tomorrow.setSeconds(0);
			tomorrow.setMilliseconds(0);
			setTimeout(updateFavicon, tomorrow.getTime() - today.getTime());
		}
		updateFavicon();
	}
});
